aws:cdk:cloudformation:props.definition:
type DeviceTable {
	PartitionKey: String!
	SortKey: String!
	Alias: String
	SensorID: String
	IsConnected: Boolean
	IsOccupied: Boolean
}

type DeviceTableConnection {
	items: [DeviceTable]
	nextToken: String
}

type Mutation {
	MutationDeviceTable(input: UpdateDeviceTableInput!): DeviceTable
}

type Query {
	queryDeviceTable(
		PartitionKey: String!,
		filter: TableDeviceTableFilterInput,
		limit: Int,
		nextToken: String
	): DeviceTableConnection
}

type Subscription {
	SubscriptionDeviceTable: DeviceTable
		@aws_subscribe(mutations: ["MutationDeviceTable"])
}

input TableDeviceTableFilterInput {
	PartitionKey: TableStringFilterInput
	SortKey: TableStringFilterInput
	Alias: TableStringFilterInput
	SensorID: TableStringFilterInput
	IsConnected: TableBooleanFilterInput
	IsOccupied: TableBooleanFilterInput
}

input TableStringFilterInput {
	ne: String
	eq: String
	le: String
	lt: String
	ge: String
	gt: String
	contains: String
	notContains: String
	between: [String]
	beginsWith: String
}

input TableBooleanFilterInput {
	ne: Boolean
	eq: Boolean
}

input UpdateDeviceTableInput {
	PartitionKey: String!
	SortKey: String!
	Alias: String
	SensorID: String
	IsConnected: Boolean
	IsOccupied: Boolean
}

aws:cdk:cloudformation:props.requestMappingTemplate:
{
    "version" : "2017-02-28",
    "operation" : "Query",
    "query" : {
        ## Provide a query expression. **
        "expression": "PartitionKey = :PartitionKey",
        "expressionValues" : {
            ":PartitionKey" : $util.dynamodb.toDynamoDBJson($ctx.args.PartitionKey)
        }
    },
    "filter": #if($context.args.filter) $util.transform.toDynamoDBFilterExpression($ctx.args.filter) #else null #end,
    "limit": $util.defaultIfNull($ctx.args.limit, 100),
    "nextToken": $util.toJson($util.defaultIfNullOrEmpty($ctx.args.nextToken, null)),
}
